package ConstructorExample;

public class defaultConstructor {
	
	int num;
	String name;

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		defaultConstructor dcons = new defaultConstructor();
		System.out.println("String value is:"+ dcons.name);
		System.out.println("int value is :" + dcons.num);

	}

}
